import React, { Component } from 'react';

class ReviewForm extends Component {

  state={
    name: '',
    rating: 0,
    review: ''
  }

  changeHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <h1>Add a Review</h1>
        <form onSubmit={console.log('yeet')} className="ui form">

          <div className="field">
            <label className="form-labels">Name</label>
            <input type="text" name="name" placeholder="Name" onChange={this.changeHandler}/>
          </div>

          <div className="field">
            <label className="form-labels">Rating</label>{
            // <div className="ui selection dropdown">
            //   <input type="hidden" name="rating" name="rating"/>
            //   <i className="dropdown icon"></i>
            //   <div className="default text">Rating</div>
            //   <div className="menu">
            //     <option className="item" data-value="1">1</option>
            //     <div className="item" data-value="2">2</div>
            //     <div className="item" data-value="3">3</div>
            //     <div className="item" data-value="4">4</div>
            //     <div className="item" data-value="5">5</div>
            //   </div>
            // </div>
          }
            <div className="ui selection dropdown">
              <input type="hidden" name="gender"/>
                <i className="dropdown icon"></i>
                <div className="default text">Gender</div>
                <div className="menu">
                  <div className="item" data-value="1">Male</div>
                  <div className="item" data-value="0">Female</div>
                </div>
              </div>
            </div>

          <div className="field">
            <label className="form-labels">Review</label>
            <textarea rows="4" cols="50" placeholder="Review" name="review"/>
          </div>

          <button className="ui button" type="submit">Submit</button>
        </form>
      </div>
    );
  }

}

export default ReviewForm;
