import React, { Component } from 'react';

const RenderReview = (props) => {
  let { review } = props

  let editClick = () => {
    alert("pew")
  }
    return (
      <div className="review-container">
        <h3>{review.name}</h3>
        <p>{review.rating} out of 5</p>
        <h4>{review.review_text}</h4>
        <button className="ui teal button" onClick={editClick}>Edit Review</button>
        <button className="negative ui button">Delete Review</button>
      </div>
    )
  }

export default RenderReview;
