import React, { Component } from 'react';

class ReviewForm extends Component {

  state={
    name: '',
    rating: 0,
    review: '',
    perfumeId: this.props.perfume.id
  }

  changeHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    {/*<div className="default text">Rating</div>
    <div className="menu">
    <option className="item" data-value="1">1</option>
    <div className="item" data-value="2">2</div>
    <div className="item" data-value="3">3</div>
    <div className="item" data-value="4">4</div>
    <div className="item" data-value="5">5</div>
    </div>*/}
    console.log(this.state)
    return (
      <div>
        <h1>Add a Review</h1>
        <form onSubmit={() => this.props.submitHandler(this.state)} className="ui form">

          <div className="field">
            <label className="form-labels">Name</label>
            <input type="text" name="name" placeholder="Name" onChange={this.changeHandler}/>
          </div>

          <div className="field">
            <label className="form-labels">Rating</label>
            <select name="rating" className="ui fluid dropdown">
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>

          <div className="field">
            <label className="form-labels">Review</label>
            <textarea rows="4" cols="50" placeholder="Review" name="review"/>
          </div>

          <button className="ui button" type="submit">Submit</button>
        </form>
      </div>
    );
  }

}

export default ReviewForm;
